import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import ActiveSession from "./ActiveSession";
import PlayStop from "./PlayStop";
import BreakDuration from "./BreakDuration";
import FocusDuration from "./FocusDuration";

function Pomodoro() {
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [focusDurationMinutes, setFocusDurationMinutes] = useState(25);
  const [breakDurationMinutes, setBreakDurationMinutes] = useState(5);
  const [focusZero, setFocusZero] = useState("");
  const [breakZero, setBreakZero] = useState("0");
  const [secondsZero, setSecondsZero] = useState("0")
  const [durationMinutes, setDurationMinutes] = useState(25);
  const [durationSeconds, setDurationSeconds] = useState(0);
  const [onBreak, setOnBreak] = useState("Focusing");

  const decreaseFocusDuration = () => {
    if (focusDurationMinutes > 5)
      setFocusDurationMinutes(minutes => {
        minutes -= 5;
        if (minutes < 10)
          setFocusZero(zero => zero = "0");
        else
          setFocusZero(zero => zero = "");
        return minutes;
      });
  }

  const increaseFocusDuration = () => {
    if (focusDurationMinutes < 60)
      setFocusDurationMinutes(minutes => {
        minutes += 5;
        if (minutes < 10)
          setFocusZero(zero => zero = "0");
        else
          setFocusZero(zero => zero = "");
        return minutes;
      });
  }

  const decreaseBreakDuration = () => {
    if (breakDurationMinutes > 1)
      setBreakDurationMinutes(minutes => {
        minutes -= 1;
        if (minutes < 10)
          setBreakZero(zero => zero = "0");
        else
          setBreakZero(zero => zero = "");
        return minutes;
      });
  }

  const increaseBreakDuration = () => {
    if (breakDurationMinutes < 15)
      setBreakDurationMinutes(minutes => {
        minutes += 1;
        if (minutes < 10)
          setBreakZero(zero => zero = "0");
        else
          setBreakZero(zero => zero = "");
        return minutes;
      });
  }

  useInterval(
    () => {
      // What should happen when the timer is running
      setIsTimerRunning(true);
      setDurationSeconds(second => {
        second === 0 ? second = 59 : second -= 1;

        if (second === 59)
          setDurationMinutes(durationMinutes - 1);

        if (second < 10)
          setSecondsZero(zero => zero = "0");
        else
          setSecondsZero(zero => zero = "");
        return second;
      });

      if(durationMinutes === 0 && durationSeconds === 0){

      }
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setDurationMinutes(minutes => minutes = focusDurationMinutes);
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <FocusDuration focusZero={focusZero} focusDurationMinutes={focusDurationMinutes} decreaseFocusDuration={decreaseFocusDuration} increaseFocusDuration={increaseFocusDuration} />
        <BreakDuration breakZero={breakZero} breakDurationMinutes={breakDurationMinutes} decreaseBreakDuration={decreaseBreakDuration} increaseBreakDuration={increaseBreakDuration} />
      </div>
      <PlayStop playPause={playPause} classNames={classNames} isTimerRunning={isTimerRunning} />
      {isTimerRunning === true ? <ActiveSession focusDurationMinutes={focusDurationMinutes} breakDurationMinutes={breakDurationMinutes} durationMinutes={durationMinutes} secondsZero={secondsZero} durationSeconds={durationSeconds} focusZero={focusZero} onBreak={onBreak} breakZero={breakZero} /> : null}
    </div>
  );
}

export default Pomodoro;
