import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import PlayStop from "./PlayStop";
import BreakDuration from "./BreakDuration";
import FocusDuration from "./FocusDuration";

function Pomodoro() {
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [focusDurationMinutes, setFocusDurationMinutes] = useState(25);
  const [breakDurationMinutes, setBreakDurationMinutes] = useState(5);
  const [focusZero, setFocusZero] = useState("");
  const [breakZero, setBreakZero] = useState("0");
  const [secondsZero, setSecondsZero] = useState("0");
  const [minutesZero, setMinutesZero] = useState("");
  const [firstPlay, setFirstPlay] = useState(true);
  const [durationMinutes, setDurationMinutes] = useState(25);
  const [durationSeconds, setDurationSeconds] = useState(0);
  const [onBreak, setOnBreak] = useState("Focusing");
  const [durationProgress, setDurationPrograss] = useState(0);

  const decreaseFocusDuration = () => {
    if (focusDurationMinutes > 5)
      setFocusDurationMinutes(minutes => {
        minutes -= 5;
        if (minutes < 10)
          setFocusZero(zero => zero = "0");
        else
          setFocusZero(zero => zero = "");
        return minutes;
      });
  }

  const increaseFocusDuration = () => {
    if (focusDurationMinutes < 60)
      setFocusDurationMinutes(minutes => {
        minutes += 5;
        if (minutes < 10)
          setFocusZero(zero => zero = "0");
        else
          setFocusZero(zero => zero = "");
        return minutes;
      });
  }

  const decreaseBreakDuration = () => {
    if (breakDurationMinutes > 1)
      setBreakDurationMinutes(minutes => {
        minutes -= 1;
        if (minutes < 10)
          setBreakZero(zero => zero = "0");
        else
          setBreakZero(zero => zero = "");
        return minutes;
      });
  }

  const increaseBreakDuration = () => {
    if (breakDurationMinutes < 15)
      setBreakDurationMinutes(minutes => {
        minutes += 1;
        if (minutes < 10)
          setBreakZero(zero => zero = "0");
        else
          setBreakZero(zero => zero = "");
        return minutes;
      });
  }

  useInterval(
    () => {
      // What should happen when the timer is running
      setIsTimerRunning(true);
      setDurationSeconds(second => {
        second === 0 ? second = 59 : second -= 1;

        if (second === 59)
          setDurationMinutes(minutes => {
            minutes = durationMinutes - 1;
            if (minutes < 10)
              setMinutesZero(zero => zero = "0");
            else
              setMinutesZero(zero => zero = "");

            return minutes;
          });

        if (second < 10)
          setSecondsZero(zero => zero = "0");
        else
          setSecondsZero(zero => zero = "");
        return second;
      });

      if (durationMinutes === 0 && durationSeconds === 0) {
        stopButton();
        if (onBreak === "Focusing") {
          setDurationMinutes(minutes => {
            minutes = breakDurationMinutes;
            if (minutes < 10)
              setMinutesZero(zero => zero = "0");
            else
              setMinutesZero(zero => zero = "");

            return minutes;
          });
          setDurationSeconds(seconds => seconds = 0);
          setSecondsZero(zero => zero = "0");
          setOnBreak(oBreak => oBreak = "On Break");
        } else {
          setDurationMinutes(minutes => {
            minutes = focusDurationMinutes;
            if (minutes < 10)
              setMinutesZero(zero => zero = "0");
            else
              setMinutesZero(zero => zero = "");
            return minutes;
          });
          setDurationSeconds(seconds => seconds = 0);
          setSecondsZero(zero => zero = "0");
          setOnBreak(oBreak => oBreak = "Focusing");
        }
        setIsTimerRunning(true);
      }
      setDurationPrograss(currentProgress => currentProgress = ((((focusDurationMinutes - durationMinutes) * 60) + durationSeconds) ) / ((durationMinutes * 60) + durationSeconds ))
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    if (firstPlay)
      setDurationMinutes(minutes => {
        minutes = focusDurationMinutes;
        if (minutes < 10)
          setMinutesZero(zero => zero = "0");
        else
          setMinutesZero(zero => zero = "");
        setFirstPlay((prevState) => !prevState)
        return minutes;
      });
    setIsTimerRunning((prevState) => !prevState);
  }

  function stopButton() {
    setFirstPlay((prevState) => !prevState);
    setIsTimerRunning((prevState) => !prevState);
    setOnBreak(oBreak => oBreak = "Focusing");
    setDurationMinutes(minutes => {
      minutes = focusDurationMinutes;
      setDurationSeconds(seconds => seconds = 0);
      setSecondsZero(zero => zero = "0");
      if (minutes < 10)
        setMinutesZero(zero => zero = "0");
      else
        setMinutesZero(zero => zero = "");
      setFirstPlay(state => !state)
      return minutes;
    });
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <FocusDuration focusZero={focusZero} focusDurationMinutes={focusDurationMinutes} decreaseFocusDuration={decreaseFocusDuration} increaseFocusDuration={increaseFocusDuration} />
        <BreakDuration breakZero={breakZero} breakDurationMinutes={breakDurationMinutes} decreaseBreakDuration={decreaseBreakDuration} increaseBreakDuration={increaseBreakDuration} />
      </div>
      <PlayStop playPause={playPause} classNames={classNames} isTimerRunning={isTimerRunning} stopButton={stopButton} />
      <div>
      {/* TODO: This area should show only when a focus or break session is running or pauses */}
      <div className="row mb-2">
        <div className="col">
          {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
          <h2 data-testid="session-title">
            {onBreak} for {onBreak !== "On Break" ? focusZero : breakZero}{onBreak !== "On Break" ? focusDurationMinutes : breakDurationMinutes }:00 minutes
          </h2>
          {/* TODO: Update message below to include time remaining in the current session */}
          <p className="lead" data-testid="session-sub-title">
            {minutesZero}{durationMinutes}:{secondsZero}
            {durationSeconds} remaining
          </p>
        </div>
      </div>
      <div className="row mb-2">
        <div className="col">
          <div className="progress" style={{ height: "20px" }}>
            <div
              className="progress-bar"
              role="progressbar"
              aria-valuemin="0"
              aria-valuemax="100"
              aria-valuenow={durationProgress} // TODO: Increase aria-valuenow as elapsed time increases
              style={{ width: `${durationProgress}%` }} // TODO: Increase width % as elapsed time increases
            />
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}

export default Pomodoro;
